import{_ as t,c as i,o,ah as r}from"./chunks/framework.CZH7KUGC.js";const p=JSON.parse('{"title":"Décrire le mouvement d’un système","titleTemplate":"Description du mouvement","description":"","frontmatter":{"titleTemplate":"Description du mouvement"},"headers":[],"relativePath":"seconde/mouvements-et-interactions/description-du-mouvement/description-du-mouvement.md","filePath":"seconde/mouvements-et-interactions/description-du-mouvement/description-du-mouvement.md","lastUpdated":1757837857000}'),n={name:"seconde/mouvements-et-interactions/description-du-mouvement/description-du-mouvement.md"};function d(u,e,s,a,l,c){return o(),i("div",null,[...e[0]||(e[0]=[r('<h1 id="decrire-le-mouvement-d-un-systeme" tabindex="-1">Décrire le mouvement d’un système <a class="header-anchor" href="#decrire-le-mouvement-d-un-systeme" aria-label="Permalink to &quot;Décrire le mouvement d’un système&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">Méthodologie</p><p>Pour décrire le mouvement d&#39;un système, il faut :</p><ul><li>définir le système étudié puis le modéliser par un point M;</li><li>choisir un référentiel d&#39;étude pertinent ;</li><li>identifier la nature géométrique de la <strong>trajectoire</strong> du point M;</li><li>déterminer l&#39;<strong>évolution de la vitesse</strong> du point M au cours du temps;</li><li>conclure sur la nature du mouvement du point M.</li></ul></div><p>Afin de décrire ce mouvement, on partira d&#39;une vidéo ou d&#39;une chronophotographie du système en mouvement. On pourra utiliser des outils numériques pour analyser ce mouvement.</p><h2 id="trajectoire" tabindex="-1">Trajectoire <a class="header-anchor" href="#trajectoire" aria-label="Permalink to &quot;Trajectoire&quot;">​</a></h2><div class="danger custom-block"><p class="custom-block-title">Définition</p><p>La <strong>trajectoire</strong> d&#39;un point M est la courbe orientée, modélisant l&#39;ensemble des positions successives occupées par le point M au cours du temps, dans un référentiel donné.</p></div><p>La trajectoire peut être :</p><ul><li>une droite</li><li>une courbe fermée (cercle, ellipse, etc.) ou une portion de cette courbe</li><li>une courbe ouverte (parabole, hyperbole, etc.)</li><li>une courbe quelconque</li></ul><p>La nature géométrique de la trajectoire permet de qualifier le mouvement du point M.</p><table tabindex="0"><thead><tr><th>Nature de la trajectoire</th><th>Nature du mouvement</th></tr></thead><tbody><tr><td>Droite</td><td>Rectiligne</td></tr><tr><td>Cercle</td><td>Circulaire</td></tr><tr><td>Ellipse</td><td>Elliptique</td></tr><tr><td>Parabole</td><td>Parabolique</td></tr><tr><td>Courbe quelconque</td><td>Curviligne</td></tr></tbody></table><h2 id="evolution-de-la-vitesse" tabindex="-1">Evolution de la vitesse <a class="header-anchor" href="#evolution-de-la-vitesse" aria-label="Permalink to &quot;Evolution de la vitesse&quot;">​</a></h2><p>L&#39;évolution de la vitesse du point M au cours du temps permet de qualifier le mouvement du point M. On distingue plusieurs types d&#39;évolution de la vitesse :</p><ul><li>Vitesse constante</li><li>Vitesse croissante</li><li>Vitesse décroissante</li></ul><p>La nature de l&#39;évolution de la vitesse permet de qualifier le mouvement du point M.</p><table tabindex="0"><thead><tr><th>Evolution de la vitesse</th><th>Nature du mouvement</th></tr></thead><tbody><tr><td>Vitesse constante</td><td>Mouvement uniforme</td></tr><tr><td>Vitesse croissante</td><td>Mouvement accéléré</td></tr><tr><td>Vitesse décroissante</td><td>Mouvement décéléré ou ralenti</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">Remarque</p><p>En seconde, on se contentera de qualifier le mouvement d&#39;uniforme ou non uniforme.</p></div><h2 id="description-du-mouvement" tabindex="-1">Description du mouvement <a class="header-anchor" href="#description-du-mouvement" aria-label="Permalink to &quot;Description du mouvement&quot;">​</a></h2><p>Pour décrire le mouvement du point M, il faut combiner la nature de la trajectoire et la nature de l&#39;évolution de la vitesse.</p><table tabindex="0"><thead><tr><th>Nature de la trajectoire</th><th>Evolution de la vitesse</th><th>Description du mouvement</th></tr></thead><tbody><tr><td>Droite</td><td>Vitesse constante</td><td>Mouvement rectiligne uniforme</td></tr><tr><td>Droite</td><td>Vitesse croissante</td><td>Mouvement rectiligne non uniforme</td></tr><tr><td>Droite</td><td>Vitesse décroissante</td><td>Mouvement rectiligne non uniforme</td></tr><tr><td>Cercle</td><td>Vitesse constante</td><td>Mouvement circulaire uniforme</td></tr><tr><td>Cercle</td><td>Vitesse croissante</td><td>Mouvement circulaire non uniforme</td></tr><tr><td>Cercle</td><td>Vitesse décroissante</td><td>Mouvement circulaire non uniforme</td></tr><tr><td>Parabole</td><td>Vitesse constante</td><td>Mouvement parabolique uniforme</td></tr><tr><td>Parabole</td><td>Vitesse croissante</td><td>Mouvement parabolique non uniforme</td></tr><tr><td>Parabole</td><td>Vitesse décroissante</td><td>Mouvement parabolique non uniforme</td></tr><tr><td>Courbe quelconque</td><td>Vitesse constante</td><td>Mouvement curviligne uniforme</td></tr><tr><td>Courbe quelconque</td><td>Vitesse croissante</td><td>Mouvement curviligne non uniforme</td></tr><tr><td>Courbe quelconque</td><td>Vitesse décroissante</td><td>Mouvement curviligne non uniforme</td></tr></tbody></table>',18)])])}const v=t(n,[["render",d]]);export{p as __pageData,v as default};
